import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import pickADateLogo from '../logos/pick-a-date logo.png'

function ResponsiveNav ({ navLinks, background, linkHoverColor }) {
  const [ hoverIndex, setHoverIndex ] = useState(-1)
  const [ navOpen, setNavOpen ] = useState(false)

  return (
    <nav className='responsive-nav' style={{ background }}>
      <ul style={{ background }} className={navOpen ? 'active' : ''}>
        <figure onClick={() => setNavOpen(!navOpen)}>
          <img
            src={pickADateLogo}
            height='40px'
            width='auto'
            alt='pick a date'
          />
        </figure>
        {navLinks.map(link => (
          <Link to={link.path} key={link.text}>
            <li
              onMouseEnter={() => setHoverIndex(1)}
              onMouseLeave={() => hoverIndex(-1)}
            >{link.text}</li>
            <i className={link.icon}/>
          </Link>
        ))}
      </ul>
    </nav>
  )
}

export default ResponsiveNav
